class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        map<int,int> mp;
        vector<int> aa;

        int dist = n-1;

        for(auto&a:queries){

            int l = a[0] , r = a[1];

            auto it = mp.lower_bound(a[0]);

            if(it==mp.begin()){

                if(it!=mp.end() && it->first == a[0] && it->second>=a[1])
                {


                }
                else{
                    vector<map<int, int>::iterator> del;

                    while(it!=mp.end() && it->first>=a[0] && it->second<=a[1]){
                        del.push_back(it);
                        dist += it->second-it->first-1;
                        it++;
                    }

                    for(auto uu:del)
                        mp.erase(uu);

                    mp[l] = max(r,mp[l]);
                    dist -= (r-l-1);
                }

            }else{


                if(it->first>l || it==mp.end()){
                    it--;
                }

                if(it->second >= r){
                    cout<<"[]";
                }else{

                    it++;
                    vector<map<int, int>::iterator> del;

                    while(it!=mp.end() && it->first>=a[0] && it->second<=a[1]){
                        del.push_back(it);
                        dist += it->second-it->first-1;
                        it++;
                    }

                    for(auto uu:del)
                        mp.erase(uu);

                    dist -= (r-l-1);
                    mp[l] = max(r,mp[l]);
                }
            }
            // for(auto&[a,b]:mp)
            //     cout<<a<<" "<<b<<endl;
            // cout<<endl;
            aa.push_back(dist);
        }
        return aa;
    }
