class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> par;
        for (int i = 0; i < n; ++i) {
            par.insert(i);
        }

        vector<int> dp(n, 0);
        for (int i = 1; i < n; ++i) {
            dp[i] = dp[i - 1] + 1;
        }
        vector<int> anns;

        for (const auto& q : queries) {
            int l = q[0];
            int rt = q[1];

            auto lb = par.lower_bound(l + 1);
            auto ub = par.lower_bound(rt);

            par.erase(lb, ub);

            anns.push_back(par.size() - 1);
        }

        return anns;
    }
};
