class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> d(n, 0);
        for (int i = 1; i < n; ++i) {
            d[i] = d[i - 1] + 1;
        }

        set<int> p;
        for (int i = 0; i < n; ++i) {
            p.insert(i);
        }

        vector<int> r;

        for (const auto& qi : q) {
            int j = qi[0];
            int lt = qi[1];

            auto lb = p.lower_bound(j + 1);
            auto ub = p.lower_bound(lt);

            p.erase(lb, ub);

            r.push_back(p.size() - 1);
        }

        return r;
    }
};
