class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
                vector<int> ans;
        set<int> s;
        for(int i=0;i<n;i++)
        {
            s.insert(i);
        }

        for(auto v:q)
        {
            int x=v[0],y=v[1];

            auto it=s.upper_bound(x);
            auto it1=s.lower_bound(y);
            vector<int> a;
            for(auto xx=it;xx!=it1;xx++)
            {
                a.push_back(*xx);
            }

            for(auto xx:a)
            {
                s.erase(xx);
            }
            int temp=(int)s.size();
            temp--;

            ans.push_back(temp);
        }

        return ans;
    }
};
