class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> dist(n, 0);
        for (int i = 1; i < n; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        set<int> pos;
        for (int i = 0; i < n; ++i) {
            pos.insert(i);
        }

        vector<int> res(q.size());

        transform(q.begin(), q.end(), res.begin(), [&pos](const vector<int>& qry) {
            int l = qry[0];
            int r = qry[1];

            auto lb = pos.lower_bound(l + 1);
            auto ub = pos.lower_bound(r);

            pos.erase(lb, ub);

            return pos.size() - 1;
        });

        return res;
    }
};
