class Solution {
public:


     vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int k = queries.size();
        vector<int> dist(n, 0);
        vector<int> ans;
        set<int> st;

        for (int i = 0; i < n-1; ++i) dist[i+1] = dist[i] + 1;

        for (int i = 0; i < n; ++i) st.insert(i);

        for (auto qi : queries) {
            int left = qi[0];
            int right = qi[1];
            auto lb = st.lower_bound(left + 1);
            auto ub = st.lower_bound(right);
            st.erase(lb, ub);
            ans.push_back(st.size() - 1);
        }

        return ans;
    }
};
