#define pii pair<int,int>
#define f first
#define s second

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans; int best = n-1;
        set<pii> jumps;
        for (const auto& e : queries) {
            auto it = jumps.lower_bound({e[0], -e[1]});
            if (jumps.size() && it != jumps.begin()) {
                auto prev_it = prev(it);
                if (prev_it->f <= e[0] && -prev_it->s >= e[1]) {
                    ans.push_back(best);
                    continue;
                }
            }
            while (it != jumps.end() && it->first >= e[0] && -it->second <= e[1]) {
                best += (-it->second) - it->first - 1;
                it = jumps.erase(it); 
            }
            jumps.insert({e[0], -e[1]});
            best -= e[1]-e[0]-1;
            ans.push_back(best);
        }
        return ans;

    }
};
