class Solution {
public:

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;

        vector<int> st;
        for(int i = 0; i < n; i++)
            st.push_back(i);

        for(int i = 0; i < queries.size(); i++)
        {
            int x = queries[i][0], y = queries[i][1];
            // 删除 [x+1,y-1]
            auto lower_it = lower_bound(st.begin(), st.end(), x + 1);
            auto upper_it = upper_bound(st.begin(), st.end(), y - 1);
            st.erase(lower_it, upper_it);

            res.push_back(st.size() - 1);
        }

        return res;
    }
};
