class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> dp(n,0);
        for(int i=1;i<n;i++) {
            dp[i]=dp[i-1]+1;
        }
        set<int> s;
        for (int i=0;i<n;i++) {
            s.insert(i);
        }
        vector<int> ans;
        for (const auto& qi : q) {
            int l = qi[0];
            int r = qi[1];
            auto lb = s.lower_bound(l + 1);
            auto ub = s.lower_bound(r);
            s.erase(lb, ub);
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
