class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        from sortedcontainers import SortedList
        sli = SortedList(list(range(n)))
        ans = []
        for x,y in queries:
            if x in sli and y in sli:
                l = sli.bisect_right(x)
                r = sli.bisect_right(y)
                for _ in range(r - l - 1):
                    sli.pop(l)
            ans.append(len(sli) - 1)
        return ans
