class Solution:    
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        sset = set([i for i in range(n - 1)])
        nextt = [i + 1 for i in range(n - 1)]
        #print(sset)


        for a, b in queries:
            if a in sset:
                if nextt[a] < b:
                    # print(nextt, a, b)
                    start = nextt[a]
                    # print(start, a)
                    nextt[a] = b
                    for j in range(start, b):
                        sset.discard(j)                    
            #print(sset)
            ans.append(len(sset))

        return ans
