class Solution {
public:
set<int> st;
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int i=0;
        while(i<n){
            st.insert(i);
            i++;
        }
int x = queries.size();
    vector<int> ans(x);

    for (int j = 0; j < x; j++) {
        int x = queries[j][0], y = queries[j][1];
auto s = st.upper_bound(x);auto e = st.lower_bound(y);
 st.erase(s, e);
        if (st.find(x) != st.end()) {
              st.insert(*st.lower_bound(y));
        } else if (st.find(y) != st.end()) {
          st.insert(y);
        } else {
            st.insert(*st.lower_bound(y));
        }

        ans[j] = st.size() - 1;
    }

    return ans;
    }
};
