#include <vector>
#include <set>
using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int totalNodes, vector<vector<int>>& modifications) {
        vector<int> distance(totalNodes, 0);
        for (int i = 1; i < totalNodes; ++i) {
            distance[i] = distance[i - 1] + 1;
        }

        set<int> currentPositions;
        for (int i = 0; i < totalNodes; ++i) {
            currentPositions.insert(i);
        }

        vector<int> ress;
        for (const auto& update : modifications) {
            int start = update[0];
            int end = update[1];

            auto lowerIterator = currentPositions.lower_bound(start + 1);
            auto upperIterator = currentPositions.lower_bound(end);

            currentPositions.erase(lowerIterator, upperIterator);
            ress.push_back(currentPositions.size() - 1);
        }

        return ress;
    }
};
