#include <vector>
#include <set>

using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> d(n, 0);
        for (int i = 1; i < n; ++i) {
            d[i] = d[i - 1] + 1;
        }

        set<int> p;
        for (int i = 0; i < n; ++i) {
            p.insert(i);
        }

        vector<int> res;

        for (const auto& qry : q) {
            int l = qry[0];
            int r = qry[1];

            auto lb = p.lower_bound(l + 1);
            auto ub = p.lower_bound(r);

            p.erase(lb, ub);

            res.push_back(p.size() - 1);
        }

        return res;
    }
};
