class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    set<int> S; for (int i = 0; i < n; i++) S.insert(i);
    vector<int> ans;
    for (auto &q: queries) {
      int l = q[0]+1, r = q[1];
      auto it = S.lower_bound(l);
      while (it != S.end() && *it < r) {
        it = S.erase(it);
      }
      ans.push_back(S.size()-1);
    }
    return ans;
  }
};
