class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>d(n,0);
        for(int i=1;i<n;i++)
            d[i]=d[i-1]+1;

        set<int>p;
        for(int i=0;i<n;i++)
            p.insert(i);

        vector<int>result;
        for(auto &query:queries){
            int l=query[0],r=query[1];
            auto lb=p.lower_bound(l+1);
            auto ub=p.lower_bound(r);
            p.erase(lb,ub);
            result.push_back(p.size()-1);
        }
        return result;
    }
};
