class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> distances(n, 0);
        for (int index = 1; index < n; ++index) {
            distances[index] = distances[index - 1] + 1;
        }

        set<int> positions;
        for (int index = 0; index < n; ++index) {
            positions.insert(index);
        }

        vector<int> result;

        for (const auto& query : queries) {
            int left = query[0];
            int right = query[1];

            auto lowerBound = positions.lower_bound(left + 1);
            auto upperBound = positions.lower_bound(right);

            positions.erase(lowerBound, upperBound);

            result.push_back(positions.size() - 1);
        }

        return result;
    }
};
