func shortestDistanceAfterQueries(n int, queries [][]int) []int {
    ans := make([]int,len(queries))
    ps := make([]int,n)
    for i:=0;i<n;i++{
        ps[i] = i
    }

    for i,q := range queries{
        u,v := q[0],q[1]
        if u < v{
           start := sort.Search(len(ps), func(i int) bool { return ps[i] >= u+1 }) 
           end   := sort.Search(len(ps), func(i int) bool { return ps[i] >= v }) 
           if start < end{
                ps = append(ps[:start],ps[end:]...)
           }
        }
        ans[i] = len(ps)-1
    }

    return ans
}
