class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> intervals;
        for (int i = 0; i < n - 1; ++i) {
            intervals.insert({i, i + 1});
        }
        vector<int> ans;

        for (const auto& query : queries) {
            int x = query[0], y = query[1];

            while (true) {
                auto it = intervals.lower_bound({x, x});
                if (it == intervals.end()) {
                    break;
                } else if (y <= it->first) {
                    break;
                } else {
                    x = min(x,it->first);
                        y = max(it->second, y);
                    intervals.erase(it);
                }
            }

            while (true) {
                auto it = intervals.lower_bound({x, x});
                if (it == intervals.begin()) {
                    break;
                } else {
                    --it;
                    if (x >= it->second) {
                        break;
                    } else {
                        x = min(x,it->first);
                        y = max(it->second, y);
                        intervals.erase(it);
                    }
                }
            }
            intervals.insert({x, y});
            ans.push_back(intervals.size());
        }

        return ans;
    }
};
