class ListNode:
     def __init__(self, x):
        self.val = x
        self.next = None
        self.onPath = True

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        nodes = [None]*n
        previous = None
        nodes[n-1] = ListNode(n-1)
        for i in range(n-2, -1, -1):
            nodes[i] = ListNode(i)
            nodes[i].next = nodes[i+1]
        count = n-1
        ans = []
        for u, v in queries:
            if nodes[u].onPath:
                a = nodes[u].next
                if a.val < v:
                    nodes[u].next = nodes[v]
                while a.val < v:
                    a.onPath = False
                    a = a.next
                    count -= 1
            ans.append(count)
        return ans
