class Solution {
  public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
    int target = n - 1;
    TreeSet<Integer> set = new TreeSet<>();
    for (int i = 0; i < target; i++) {
      set.add(i);
    }

    int[] result = new int[queries.length];
    int index = 0;
    for (int[] query : queries) {
      int left = query[0];
      int right = query[1];
      Set<Integer> subset = new HashSet<>(set.subSet(left + 1, right));
      for (int node : subset) {
        set.remove(node);
      }
      result[index++] = set.size();
    }
    return result;
  }
}
