class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        vector<int> dp(n, 0);

        for (int i = 1; i < n; i++) {
            dp[i] = dp[i - 1] + 1;
        }

        set<int> prev;

        for (int i = 0; i < n; i++) {
            prev.insert(i);
        }

        vector<int> res;

        for (const auto& q : queries) {
            int from = q[0];
            int to = q[1];

            auto first = prev.lower_bound(from + 1);
            auto last = prev.lower_bound(to);

            prev.erase(first, last);

            res.push_back(prev.size() - 1);
        }

        return res;

    }
};

