class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {


        set<int> rem_nodes;
        for (int i = 0; i < n; ++i) {
            rem_nodes.insert(i);
        }

        vector<int> dist(n, 0);
        for (int i = 1; i < n; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        vector<int> ans;

        for (int i = 0; i < queries.size(); i++) {
            int l = queries[i][0];
            int r = queries[i][1];

            auto left = rem_nodes.lower_bound(l + 1);
            auto right = rem_nodes.lower_bound(r);

            rem_nodes.erase(left, right);

            ans.push_back(rem_nodes.size() - 1);
        }

        return ans;
    }
};
