class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> next(n);
        unordered_set<int> s;
        for(int i = 0; i < n; ++i){
            s.insert(i);
            if(i != n-1)
                next[i] = i + 1;
        }

        vector<int> res;
        for(auto q: queries){
            int u = q[0], v = q[1];

            if(s.count(u) && s.count(v)){
                for(int i = next[u]; i < v; i = next[i])
                    s.erase(i);
            }
            next[u] = v;
            res.push_back(s.size() - 1);
        }

        return res;
    }
};
