class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> a(n);
        vector<int> ans;
        iota(a.begin(),a.end(),0);
        int l=0,r=n-1;
        for(auto i:queries){
            int x=i[0];
            int y=i[1];
            auto it1=lower_bound(a.begin(),a.end(),x);
            if(*it1>x)it1--;
            auto it2=lower_bound(a.begin(),a.end(),y);
            a.erase(it1+1,it2);
            ans.push_back(a.size()-1);
        }
        return ans;
    }
};
