class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> res;
        for (auto &q : queries) {
            auto t = s.upper_bound(q[0]);
            while (t != s.end() && *t < q[1]) {
                t = s.erase(t);
            }
            res.push_back(s.size() - 1);
        }
        return res;
    }
};
