class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        // vector<int> dp(n,0);
        // for(int i=0;i<n;i++)
        // {
        //     // dp[i] = dp[i-1]+1;
        // }

        set<int> s;

        for(int i=0;i<n;i++)
        {
            s.insert(i);
        }

        vector<int> solution;

        for(auto it: queries)
        {
            int u = it[0];
            int v = it[1];

            auto lb = s.lower_bound(u+1);
            auto ub = s.lower_bound(v);

            s.erase(lb, ub);

            solution.push_back(s.size()-1);
        }

        return solution;

    }
};
