class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int dp = n-1;
        vector<int> result;
        vector<int> visit(n,1), pre(n, 0);
        for(int i=1; i<n; i++) pre[i] = i-1;
        for(auto x:queries){
            if(!(visit[x[0]] && visit[x[1]])){
                result.push_back(dp);
                continue;
            }
            for(int i=pre[x[1]]; i>x[0];){
                visit[i]=0;
                dp--;
                i = pre[i];
            }
            pre[x[1]] = x[0];
            result.push_back(dp);
        }
        return result;
    }
};
