class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m=q.size(),i,j,x,y;
        vector<int> a(m),b(n);
        set<int> s;
        for(i=0;i<n;i++){
            s.insert(i);
            b[i]=i;
        }
        for(i=0;i<m;i++){
            x=q[i][0],y=q[i][1];
            if(s.find(x)!=s.end()&&s.find(y)!=s.end()){
                auto k=s.lower_bound(x);
                *++k;
                unordered_set<int> r;
                while((*k)<y){
                    r.insert(*k);
                    *++k;
                }
                for(auto z=r.begin();z!=r.end();z++){
                    s.erase(*z);
                }
            }
            a[i]=s.size()-1;
        }
        return a;
    }
};
