class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector < int > v;
        map < int, int > mp; mp[-1] = 0;
        int ans = n-1; n = queries.size();
        for(int i = 0; i < n; i++){
            int x = queries[i][0], y = queries[i][1];
            auto uu = mp.upper_bound(x); uu--;
            if(uu->first != -1 && uu->first+uu->second > x && uu->second >= (y-x-1)){
                v.push_back(ans); continue;
            }
            auto u = mp.lower_bound(x);
            set < int > s;
            while(!mp.empty() && u != mp.end() && u->first < y){
                ans += u->second;
                s.insert(u->first);
                u++;
            }
            for(auto u : s) mp.erase(u);
            ans -= (y-x-1);
            mp[x] = (y-x-1);
            v.push_back(ans);
        }
        return v;
    }
};
