class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> open;
        for (int i = 0; i < n; i++) {
            open.insert(i);
        }

        vector<int> res;
        for (auto& query : queries) {
            auto it1 = open.find(query[0]);
            auto it2 = open.find(query[1]);

            if (it1 != open.end() && it2 != open.end()) {
                ++it1;
                //cout << "Erasing [" << *it1 << ", " << *it2 << ")" << endl;
                open.erase(it1, it2);
            }
            res.push_back(open.size() - 1);
        }

        return res;
    }
};
