class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n, 0);
        for (int i = 1; i < n; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        set<int> x;
        for (int i = 0; i < n; ++i) {
            x.insert(i);
        }

        vector<int> y;

        for (const auto& qi : queries) {
            int l = qi[0];
            int rt = qi[1];

            auto lb = x.lower_bound(l + 1);
            auto ub = x.lower_bound(rt);

            x.erase(lb, ub);

            y.push_back(x.size() - 1);
        }

        return y;

    }
};
