class Solution {
struct ListNode{
    int val;
    ListNode* next;
    ListNode(int v): val(v), next(nullptr){}
};
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        ListNode* preHead = new ListNode(-1);
        ListNode* pre = preHead;
        vector<ListNode*> map;
        for(int i = 0; i < n; i++) {
            ListNode* curr = new ListNode(i);
            pre->next = curr;
            map.push_back(curr);
            pre = curr;
        }

        vector<int> res;
        int path = n-1;
        for(auto q: queries) {
            int from = q[0];
            int to = q[1];
            auto toNode = map[from]->next;
            while(toNode && toNode->val < to) {
                path--;
                auto next = toNode->next;
                toNode->next = nullptr;
                toNode = next;
            }

            map[from]->next = toNode;
            res.push_back(path);
        }
        return res;
    }
};
