from sortedcontainers import SortedList as SL

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        sl = SL([i for i in range(n)])

        res = []

        for x,y in queries:
            i = sl.bisect_right(x)
            while i<len(sl) and sl[i]<y:
                # print(f"{x} {y} {i} {sl[i]} is removed")
                sl.remove(sl[i])
            res.append(len(sl)-1)

        return res
