import sortedcontainers
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        #There are no overlaps (except for on same element which is fine)
        #Therefore, if you skip over any numbers, remove them permanently.
        #Then the answer is just how many numbers are in the set remaining.


        s = sortedcontainers.SortedSet([i + 1 for i in range(n - 1)])
        res = []
        for x, y in queries:
            idx = s.bisect_right(x)
            while idx < len(s) and s[idx] < y:
                if s[idx] > x:
                    s.remove(s[idx])
            res.append(len(s))
        return res
