class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int x,y,tmp=n-1,id=-1;
        vector<int> dp(n),ans(queries.size());
        vector<bool> f(n,true);
        for(int i=0;i<n;++i) dp[i]=i+1;
        for(const auto& ele:queries){
            x=ele[0];y=ele[1];
            if(f[x]){
                for(int i=dp[x];i!=y;i=dp[i]){
                    if(i>=y) break;
                    f[i]=false;
                    --tmp;
                }
                dp[x]=y;
            }
            ans[++id]=tmp;
        }
        return ans;
    }
};
