class Solution
{
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>> &queries)
    {
        set<int> lookup;
        for (int i = 1; i < n; i++)
        {
            lookup.insert(i);
        }
        vector<int> result;
        result.reserve(queries.size());
        for (auto &query : queries)
        {
            int from = query[0];
            int to = query[1];
            auto low = lookup.upper_bound(from);
            auto high = lookup.lower_bound(to);
            for (auto it = low; it != high;)
            {
                it = lookup.erase(it);
            }
            result.emplace_back(lookup.size());
        }
        return result;
    }
};
