class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int[] par = new int[n];
        for(int i=0; i<n; i++){
            par[i] = i-1;
        }
        int ans[] = new int[queries.length];
        int sh = n-1;

        for(int i=0; i<queries.length; i++){
            if(queries[i][0]<par[queries[i][1]] && par[queries[i][1]]!=-1){
                int p = 1;
                int c = queries[i][1];
                while(par[c]!=queries[i][0]){
                    int z = c;
                    c = par[c];
                    par[z] = -1;
                    p++;
                }
                sh = sh - p+1;
                par[queries[i][1]] = queries[i][0];
                // for(int m=queries[i][0]+1; m<queries[i][1]; m++){
                //     par[m] = -1;
                // }
            }
            ans[i] = sh;
        }


        return ans;
    }
}
