class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<bool> exists(n, true);
        map<int, int> visitedMap;
        vector<int> ans;
        int cost = n - 1;
        for (const auto& query : queries) {
            int left = query[0], right = query[1];
            if (!exists[left]) {
                if (exists[right]) {
                    cout << "Why does right exist?\n";
                }
                ans.push_back(cost);
                continue;
            }
            for (int i = left + 1; i < right; i++) {
                if (visitedMap[i])
                    i = visitedMap[i];
                else if (exists[i]) {
                    exists[i] = false;
                    cost--;
                }
            }
            visitedMap[left + 1] = right - 1;
            ans.push_back(cost);
        }
        return ans;
    }
};
