from sortedcontainers import SortedSet

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ss = SortedSet(range(n))
        ans = []
        for i, j in queries:
            k = ss.bisect_left(i + 1)
            while ss[k] < j:
                del ss[k]
            ans.append(len(ss) - 1)
        return ans
