class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i=1; i<n; ++i) {
            s.insert(i);
        }
        vector<int> ans;
        for (auto q : queries) {
            int u = q[0], v = q[1];
            if (u > v) {
                swap(u, v);
            }
            auto it = s.lower_bound(u+1);
            while (it != s.lower_bound(v)) {
                auto it1 = it;
                it1++;
                u = *it1;
                s.erase(it);
                it = s.lower_bound(u);
            }
            ans.push_back(s.size());
        }
        return ans;
    }
};
