class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        from sortedcontainers import SortedList

        sorted_list = SortedList(list(range(n)))
        res = []
        for u, v in queries:
            start = sorted_list.bisect_left(u)
            if sorted_list[start] != u:
                res.append(res[-1])
            else:
                end = sorted_list.bisect_left(v)
                if sorted_list[end] != v:
                    res.append(res[-1])
                else:
                    del sorted_list[start+1:end]
                    res.append(len(sorted_list)-1)

        return res
