class Solution {
public:
    struct node {
        int val;
        node* next = 0;
        node(int i, node* nx) {
            val = i;
            next = nx;
        }
    };
    vector<int> shortestDistanceAfterQueries(int n,
                                             vector<vector<int>>& queries) {
        int distance = n - 1;
        vector<int> res;
        vector<node*> pointer(n, nullptr);
        node *temp = nullptr, *temp2 = nullptr, *ptrv0 = nullptr;
        for (int i = n - 1; i >= 0; --i) {
            temp = new node(i, temp);
            pointer[i] = temp;
        }
        for (auto& v : queries) {
            if (pointer[v[0]] == nullptr || pointer[v[1]] == nullptr) {

            } else {
                ptrv0 = pointer[v[0]];
                temp = ptrv0->next;
                while (temp->val != v[1]) {
                    temp2 = temp;
                    temp = temp->next;
                    --distance;
                    pointer[temp2->val] = nullptr;
                    delete temp2;
                }
                ptrv0->next = temp;
            }
            res.push_back(distance);
        }
        return res;
    }
};
