class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int sz=queries.size();
        set<int> st;
        vector<int> ans;
        for(int i=1;i<=n;i++) st.insert(i);
        for(int i=0;i<sz;i++){
            int x=queries[i][0],y=queries[i][1];
            while(1){
                auto it=st.lower_bound(x+1);
                if(it==st.end() || *it>=y) break;
                else st.erase(it);
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
