class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        now = n-1
        dic = dict()
        for i in range(n-1):
            dic[i] = i+1

        for q in queries:
            if(dic[q[0]] > q[1]):
                ans.append(now)
            else:
                cnt = 0
                temp = q[0]
                while temp != q[1]:
                    cnt+=1
                    nowtemp = temp
                    temp = dic[temp]
                    dic[nowtemp] = q[1]
                now-=cnt-1
                dic[q[0]] = q[1]
                ans.append(now)

        return ans
