class Solution {
public:
struct Node {
    int val;
    Node* next;
    Node(int value) : val(value), next(nullptr) {}
};
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int,Node*> st;
        vector<int> res;
        Node* prev = new Node(0);
        st[0]=prev;
        for(int i=1;i<n;i++){
            Node* newNode = new Node(i);
            prev->next=newNode;
            prev=newNode;
            st[i]=newNode;
        }
        for(int i=0;i<queries.size();i++){
            if(st.find(queries[i][0])!=st.end() && st.find(queries[i][1])!=st.end() ){
                Node* temp=st[queries[i][0]]->next;
                while(temp->val<queries[i][1]){
                    st.erase(temp->val);
                    temp=temp->next;
                }
                st[queries[i][0]]->next=st[queries[i][1]];

                // int a=queries[i][0]+1;
                // while(a<queries[i][1] && st.find(a)!=st.end()){
                //     cout<<a<<endl;
                //     st.erase(a++);
                // }
                // a=queries[i][1]-1;
                // while(a>queries[i][0] && st.find(a)!=st.end()){
                //     cout<<a<<endl;
                //     st.erase(a--);
                // }
            }
            res.push_back(st.size()-1);
        }
        return res;
    }
};
