class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>t(n,0),uttar;
        for(int i=1;i<n;i++){
            t[i]=t[i-1]+1;
        }
        set<int>h;
        for(int i=0;i<n;i++){
            h.insert(i);
        }
        for(auto &v:queries){
            int nb=v[0];
            int unb=v[1];
            auto nwb=h.lower_bound(nb+1);
            auto uwbb=h.lower_bound(unb);

            h.erase(nwb,uwbb);
            int y=h.size()-1;
            uttar.push_back(y);
        }
        return uttar;
    }
};
