class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        list<int> ls;
        unordered_map<int, list<int>::iterator> m;
        for (int i = 0; i + 1 < n; ++i) {
            ls.push_back(i);
            m[i] = --ls.end();
        }

        vector<int> ans;
        for (auto& q : queries) {
            if (m.count(q[0])) {
                auto it = m[q[0]];
                ++it;
                while (it != ls.end() && *it < q[1]) {
                    m.erase(*it);
                    it = ls.erase(it);
                }
            }

            ans.push_back(ls.size());
        }
        return ans;
    }
};
