class Solution {
public:

        vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> dist(n, 0);
        for (int i = 1; i < n; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        set<int> st;
        for (int i = 0; i < n; ++i) {
            st.insert(i);
        }

        vector<int> ans;

        for (const auto& it : q) {
            int a = it[0];
            int b = it[1];

            auto lb = st.lower_bound(a + 1);
            auto ub = st.lower_bound(b);

            st.erase(lb, ub);

            ans.push_back(st.size() - 1);
        }

        return ans;

    }

};
