class Solution {
public:
    vector<int> solve(int n, vector<vector<int>>& queries) {
        vector<int> distances(n, 0);
        for (int i = 1; i < n; ++i) {
            distances[i] = distances[i - 1] + 1;
        }

        set<int> positions;
        for (int i = 0; i < n; ++i) {
            positions.insert(i);
        }

        vector<int> result;

        for (const auto& query : queries) {
            int left = query[0];
            int right = query[1];

            auto lower_bound = positions.lower_bound(left + 1);
            auto upper_bound = positions.lower_bound(right);

            positions.erase(lower_bound, upper_bound);

            result.push_back(static_cast<int>(positions.size()) - 1);
        }

        return result;
    }

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        return solve(n, queries);
    }
};
